                                  />
                                </Button>
                              </InputGroup.Text>
                            </InputGroup.Append>
                            <InputGroup.Append>
                              <InputGroup.Text>
                                <Overlay
                                  target={invalidMessageRef}
                                  show={emptyMessageCheck}
                                  placement="top"
                                >
                                  {(props) => (
                                    <Tooltip id="chat-invalid" {...props}>
