      return {
        ...state,
        tipStripe: {
          data: {},
          loading: true,
          error: action.error,
          inputData: {},
          loadingButtonContent: null,
          buttonDisable: false,
        },
      };
      case SEND_TIP_BY_PAYPAL_START:
        return {
          ...state,
          tipPaypal: {
            data: {},
