        case SEND_TIP_BY_COINPAYMENT_FAILURE:
          return {
            ...state,
            tipCoinPayment: {
              data: {},
              loading: true,
              error: action.error,
              inputData: {},
              loadingButtonContent: null,
              buttonDisable: false,
            },
          };

    default:
      return state;
  }
};

export default SendTipReducer;
